import React from "react";
import { Text, StyleSheet, View, FlatList } from "react-native";

const ListScreen = () => {
  const friends = [
    { name: "friend1", age: 20 },
    { name: "friend2", age: 45 },
    { name: "friend3", age: 32 },
    { name: "friend4", age: 27 },
    { name: "friend5", age: 64 },
    { name: "friend6", age: 12 },
    { name: "friend7", age: 46 },
    { name: "friend8", age: 10 },
    { name: "friend9", age: 70 },
    { name: "friend10", age: 25 },
  ];
  // console.log(friends);
  return (
    <FlatList
      horizontal={false}
      showsHorizontalScrollIndicator={false}
      keyExtractor={(friend) => friend.name}
      data={friends}
      renderItem={({ item }) => {
        return (
          <Text style={styles.textStyle}>
            {item.name} - Age {item.age}
          </Text>
        );
      }}
    />
  );
};

const styles = StyleSheet.create({
  textStyle: {
    fontSize: 15,
    marginVertical: 30,
  },
});

export default ListScreen;
